mapper.select:
select * 
from Item 
where location like #{location}%;


select COUNT(*)
from Item 
where itemId = #{itemID}

mapper.update:

Update Item 
set
Type=#{Type}, mbti=#{mbti}, price=#{price}, itemName=#{itemName}, location=#{location}, tel=#{tel}, contents=#{contents},view=#{view}
where itemID=#{itemID}

mapper.select:

			SELECT (@rownum := @rownum + 1) AS itemID, I.*
			FROM (
    				SELECT @rownum := #{startRowIndex}
   					 FROM dual
				) tmp
			JOIN Item I
			ORDER BY updateDate DESC
			LIMIT #{rowsPerPage} OFFSET #{startRowIndex}
